
public class stringManipulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String str="this years love";
		
		
		
		System.out.println(str.substring(0));//this years love
		System.out.println(str.substring(0, 4));//this    start with 0 index but fetch 4 characters
		
		
	

	}

}
